import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    const sanitizedUsername = username.trim().toLowerCase(); // üîπ Rimuove spazi extra e converte in minuscolo

    if (!sanitizedUsername) {
      alert("‚ö†Ô∏è Inserisci un username valido!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: sanitizedUsername, password }), // ‚úÖ Invia il nome sanificato
      });

      const data = await response.json();

      if (response.ok) {
        alert(`‚úÖ Benvenuto, ${data.user.username}!`);
        navigate(`/exercises/${data.user.username}`);
      } else {
        alert(`‚ùå ${data.message}`);
      }
    } catch (error) {
      console.error("Errore durante il login:", error);
      alert("‚ùå Errore durante il login.");
    }
  };

  return (
    <div className="container">
      <h2>Login</h2>
      <input
        type="text"
        placeholder="Inserisci username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        type="password"
        placeholder="Inserisci password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleLogin}>Accedi</button>
    </div>
  );
};

export default Login;
