import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ExerciseForm from "./ExerciseForm";
import "./ExercisePage.css"; // Importa il CSS

const ExercisePage = () => {
  const { username } = useParams();
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);

  // ✅ Funzione per recuperare gli esercizi dell'utente
  const fetchExercises = async () => {
    try {
      const response = await fetch(`http://localhost:5000/exercises/${username}`);
      const data = await response.json();

      if (response.ok) {
        setExercises(data);
      } else {
        alert(`❌ Errore: ${data.message}`);
      }
    } catch (error) {
      console.error("❌ Errore nel recupero degli esercizi:", error);
      alert("❌ Errore nel recupero degli esercizi.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Funzione per rimuovere un esercizio
  const handleDeleteExercise = async (exerciseId) => {
    try {
      const response = await fetch(`http://localhost:5000/exercise/${username}/${exerciseId}`, {
        method: "DELETE",
      });

      const data = await response.json();

      if (response.ok) {
        alert("✅ Esercizio rimosso con successo!");
        fetchExercises(); // Ricarica la lista degli esercizi
      } else {
        alert(`❌ Errore: ${data.message}`);
      }
    } catch (error) {
      console.error("❌ Errore durante la rimozione dell'esercizio:", error);
      alert("❌ Errore durante la rimozione dell'esercizio.");
    }
  };

  useEffect(() => {
    fetchExercises();
  }, [username]);

  return (
    <div className="exercise-page">
      <h2>Esercizi di {username}</h2>

      {/* ✅ Mostra un messaggio se gli esercizi sono ancora in caricamento */}
      {loading ? (
        <p>Caricamento in corso...</p>
      ) : exercises.length === 0 ? (
        <p>⚠️ Nessun esercizio registrato.</p>
      ) : (
        <div className="exercise-table-container">
          <table className="exercise-table">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Peso (kg)</th>
                <th>Ripetizioni</th>
                <th>Data</th>
                <th>Azioni</th> {/* Nuova colonna per il pulsante "Rimuovi" */}
              </tr>
            </thead>
            <tbody>
              {exercises.map((exercise) => (
                <tr key={exercise._id}>
                  <td>{exercise.name}</td>
                  <td>{exercise.weight}</td>
                  <td>{exercise.reps}</td>
                  <td>{new Date(exercise.date).toLocaleDateString()}</td>
                  <td>
                    {/* Pulsante per rimuovere l'esercizio */}
                    <button
                      className="delete-button"
                      onClick={() => handleDeleteExercise(exercise._id)}
                    >
                      Rimuovi
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* ✅ Modulo per aggiungere esercizi */}
      <ExerciseForm username={username} fetchExercises={fetchExercises} />
    </div>
  );
};

export default ExercisePage;
