// src/components/ExercisePage.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ExerciseForm from "./ExerciseForm";

const ExercisePage = () => {
  const { username } = useParams();
  const [exercises, setExercises] = useState([]);

  useEffect(() => {
    fetchExercises();
  }, [fetchExercises]);  
  

  const fetchExercises = async () => {
    try {
      const response = await fetch(`http://localhost:5000/user/${username}`);
      const data = await response.json();

      if (response.ok) {
        setExercises(data.exercises);
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Errore nel recupero degli esercizi:", error);
      alert("Errore nel recupero degli esercizi.");
    }
  };

  return (
    <div>
      <h2>Esercizi di {username}</h2>
      <ul>
        {exercises.map((exercise, index) => (
          <li key={index}>
            {exercise.name} - {exercise.weight} kg - {exercise.reps} reps - {new Date(exercise.date).toLocaleDateString()}
          </li>
        ))}
      </ul>
      <ExerciseForm username={username} fetchExercises={fetchExercises} />
    </div>
  );
};

export default ExercisePage;
