// src/components/ExerciseForm.js
import { useState } from "react";

const ExerciseForm = ({ username, fetchExercises }) => {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState("");
  const [reps, setReps] = useState("");

  const handleAddExercise = async () => {
    if (!name || !weight || !reps) {
      alert("Compila tutti i campi prima di aggiungere un esercizio!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/exercise", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, name, weight, reps }),
      });

      const data = await response.json();

      if (response.ok) {
        alert("✅ Esercizio aggiunto con successo!");
        fetchExercises(); // Aggiorna la lista esercizi
        setName(""); // Reset campi input
        setWeight("");
        setReps("");
      } else {
        alert(`❌ Errore: ${data.message}`);
      }
    } catch (error) {
      console.error("❌ Errore durante l'aggiunta dell'esercizio:", error);
      alert("Errore del server. Riprova più tardi.");
    }
  };

  return (
    <div>
      <h3>Aggiungi Esercizio</h3>
      <select value={name} onChange={(e) => setName(e.target.value)}>
        <option value="">Seleziona esercizio</option>
        <option value="Panca Piana">Panca Piana</option>
        <option value="Stacco">Stacco</option>
        <option value="Squat">Squat</option>
      </select>
      <input
        type="number"
        placeholder="Peso (kg)"
        value={weight}
        onChange={(e) => setWeight(e.target.value)}
      />
      <input
        type="number"
        placeholder="Ripetizioni"
        value={reps}
        onChange={(e) => setReps(e.target.value)}
      />
      <button onClick={handleAddExercise}>Aggiungi</button>
    </div>
  );
};

export default ExerciseForm;
