import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Register.css";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Funzione per la registrazione
  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage(""); // Resetta eventuali messaggi precedenti

    const sanitizedUsername = username.trim().toLowerCase();
    if (!sanitizedUsername) {
      setMessage("⚠️ Inserisci un username valido!");
      return;
    }

    if (password !== confirmPassword) {
      setMessage("❌ Le password non coincidono!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: sanitizedUsername, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("✅ Registrazione completata! Ora puoi accedere.");
        setUsername("");
        setPassword("");
        setConfirmPassword("");

        setTimeout(() => {
          navigate("/login"); // Dopo qualche secondo, porta l'utente al login
        }, 2000);
      } else {
        setMessage(`❌ ${data.message}`);
      }
    } catch (error) {
      console.error("Errore durante la registrazione:", error);
      setMessage("❌ Errore di connessione. Riprova.");
    }
  };

  return (
    <div className="register-container">
      <h1 className="site-title">GymProgress</h1>
      <h3 className="site-subtitle">Registra i tuoi risultati</h3>

      <div className="register-box">
        <h2>Registrazione</h2>

        {message && <p className="error-message">{message}</p>}

        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="Scegli un username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Scegli una password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Conferma password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
          <button type="submit">Registrati</button>
        </form>

        <p className="login-link">
          Hai già un account?{" "}
          <span onClick={() => navigate("/login")}>Accedi qui</span>
        </p>
      </div>
    </div>
  );
};

export default Register;
