import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom"; // Hook per navigazione
import ExerciseForm from "./ExerciseForm";
import "./ExercisePage.css"; // Importa il CSS

const ExercisePage = () => {
  const { username } = useParams();
  const [exercises, setExercises] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate(); 

  // ✅ Recupera gli esercizi dell'utente
  const fetchExercises = async () => {
    try {
      const response = await fetch(`http://localhost:5000/exercises/${username}`);
      const data = await response.json();

      if (response.ok) {
        setExercises(data);
      } else {
        alert(`❌ Errore: ${data.message}`);
      }
    } catch (error) {
      console.error("❌ Errore nel recupero degli esercizi:", error);
      alert("❌ Errore nel recupero degli esercizi.");
    } finally {
      setLoading(false);
    }
  };

  // ✅ Rimuove un esercizio
  const handleDeleteExercise = async (exerciseId) => {
    try {
      const response = await fetch(`http://localhost:5000/exercise/${username}/${exerciseId}`, {
        method: "DELETE",
      });

      const data = await response.json();

      if (response.ok) {
        alert("✅ Esercizio rimosso con successo!");
        fetchExercises(); 
      } else {
        alert(`❌ Errore: ${data?.message || 'Errore sconosciuto'}`);
      }
    } catch (error) {
      console.error("❌ Errore durante la rimozione dell'esercizio:", error);
      alert("❌ Errore durante la rimozione dell'esercizio.");
    }
  };

  // ✅ Funzione Logout
  const handleLogout = () => {
    console.log("Logout in corso...");
    localStorage.removeItem("user"); 
    navigate("/", { replace: true }); // Torna alla Home con Login e Registrazione
  };

  // ✅ Carica gli esercizi al montaggio
  useEffect(() => {
    fetchExercises();
  }, [username]);

  return (
    <div className="exercise-page">
      {/* ✅ Pulsante di Logout in alto a destra */}
      <button className="logout-button" onClick={handleLogout}>Logout</button>

      <h2>Esercizi di {username}</h2>

      {/* ✅ Modulo per aggiungere esercizi - Ora è sopra la tabella */}
      <ExerciseForm username={username} fetchExercises={fetchExercises} />

      {/* ✅ Mostra un messaggio se gli esercizi sono in caricamento */}
      {loading ? (
        <p>Caricamento in corso...</p>
      ) : exercises.length === 0 ? (
        <p>⚠️ Nessun esercizio registrato.</p>
      ) : (
        <div className="exercise-table-container">
          <table className="exercise-table">
            <thead>
              <tr>
                <th>Nome</th>
                <th>Peso (kg)</th>
                <th>Ripetizioni</th>
                <th>Data</th>
                <th>Azioni</th>
              </tr>
            </thead>
            <tbody>
              {exercises.map((exercise) => (
                <tr key={exercise._id}>
                  <td>{exercise.name}</td>
                  <td>{exercise.weight}</td>
                  <td>{exercise.reps}</td>
                  <td>{new Date(exercise.date).toLocaleDateString()}</td>
                  <td>
                    {/* ✅ Pulsante per rimuovere l'esercizio */}
                    <button
                      className="delete-button"
                      onClick={() => handleDeleteExercise(exercise._id)}
                    >
                      Rimuovi
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default ExercisePage;
