import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginUsername, setLoginUsername] = useState("");
  const [loginPassword, setLoginPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  // Funzione per la registrazione
  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage(""); // Resetta eventuali messaggi precedenti

    const sanitizedUsername = username.trim().toLowerCase(); // üîπ Rimuove spazi e converte in minuscolo

    if (!sanitizedUsername) {
      setMessage("‚ö†Ô∏è Inserisci un username valido!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: sanitizedUsername, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(`‚úÖ Utente registrato con successo! Effettua il login.`);
        setUsername("");
        setPassword("");
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (error) {
      console.error("Errore durante la registrazione:", error);
      setMessage("‚ùå Errore durante la registrazione.");
    }
  };

  // Funzione per il login
  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage(""); // Resetta eventuali messaggi precedenti

    const sanitizedLoginUsername = loginUsername.trim().toLowerCase(); // üîπ Rimuove spazi e converte in minuscolo

    if (!sanitizedLoginUsername) {
      setMessage("‚ö†Ô∏è Inserisci un username valido!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: sanitizedLoginUsername, password: loginPassword }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(`‚úÖ Accesso effettuato con successo! Benvenuto ${data.user.username}`);
        navigate(`/exercises/${data.user.username}`);
      } else {
        setMessage(`‚ùå ${data.message}`);
      }
    } catch (error) {
      console.error("Errore durante il login:", error);
      setMessage("‚ùå Errore durante il login.");
    }
  };

  return (
    <div className="container">
      <h2>GymProgress - Registra i tuoi risultati</h2>

      {/* Sezione Registrazione */}
      <div className="form-container" style={{ marginBottom: "30px" }}>
        <h3>Registrazione</h3>
        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Registrati</button>
        </form>
      </div>

      {/* Sezione Login */}
      <div className="form-container">
        <h3>Accedi</h3>
        <form onSubmit={handleLogin}>
          <input
            type="text"
            placeholder="Username"
            value={loginUsername}
            onChange={(e) => setLoginUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={loginPassword}
            onChange={(e) => setLoginPassword(e.target.value)}
            required
          />
          <button type="submit">Accedi</button>
        </form>
      </div>

      {/* Messaggio di stato */}
      {message && <p>{message}</p>}
    </div>
  );
};

export default Register;
