import { useState } from "react";
import { useNavigate } from "react-router-dom";

const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [message, setMessage] = useState("");
  const [acceptedPrivacy, setAcceptedPrivacy] = useState(false); // ✅ Nuovo stato per la checkbox
  const navigate = useNavigate();

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage("");

    const sanitizedUsername = username.trim().toLowerCase();
    if (!sanitizedUsername) {
      setMessage("⚠️ Inserisci un username valido!");
      return;
    }

    try {
      const response = await fetch("http://localhost:5000/register", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username: sanitizedUsername, password }),
      });

      const data = await response.json();

      if (response.ok) {
        setMessage("✅ Utente registrato con successo! Ora puoi accedere.");
        setUsername("");
        setPassword("");
        setAcceptedPrivacy(false); // ✅ Resetta la checkbox dopo la registrazione
      } else {
        setMessage(`❌ ${data.message}`);
      }
    } catch (error) {
      console.error("Errore durante la registrazione:", error);
      setMessage("❌ Errore durante la registrazione.");
    }
  };

  return (
    <div className="container">
      <h2>GymProgress - Registra i tuoi risultati</h2>

      <div className="form-container" style={{ marginBottom: "30px" }}>
        <h3>Registrazione</h3>
        <form onSubmit={handleRegister}>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          {/* ✅ Checkbox Privacy */}
          <div style={{ display: "flex", alignItems: "center", marginTop: "10px" }}>
            <input
              type="checkbox"
              id="privacyPolicy"
              checked={acceptedPrivacy}
              onChange={(e) => setAcceptedPrivacy(e.target.checked)}
              required
            />
            <label htmlFor="privacyPolicy" style={{ marginLeft: "5px" }}>
              Ho letto e accetto l'
              <a href="/privacy" target="_blank" rel="noopener noreferrer">
                Informativa sulla Privacy
              </a>
            </label>
          </div>

          {/* ✅ Disabilita il pulsante se la checkbox non è selezionata */}
          <button type="submit" disabled={!acceptedPrivacy}>
            Registrati
          </button>
        </form>
      </div>

      {message && <p>{message}</p>}
    </div>
  );
};

export default Register;
