// src/components/ExercisePage.js
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ExerciseForm from "./ExerciseForm";

const ExercisePage = () => {
  const { username } = useParams();
  const [exercises, setExercises] = useState([]);

  // Funzione per ottenere gli esercizi
  const fetchExercises = async () => {
    try {
      const response = await fetch(`http://localhost:5000/exercises/${username}`);
      const data = await response.json();

      if (response.ok) {
        setExercises(data);
      } else {
        alert(data.message);
      }
    } catch (error) {
      console.error("Errore nel recupero degli esercizi:", error);
      alert("Errore nel recupero degli esercizi.");
    }
  };

  // Chiamata per ottenere gli esercizi all'inizio
  useEffect(() => {
    fetchExercises();
  }, [username]);

  return (
    <div>
      <h2>Esercizi di {username}</h2>

      {/* Tabella degli esercizi */}
      <div className="exercise-table">
        <h3>Storico Esercizi</h3>
        <table>
          <thead>
            <tr>
              <th>Nome Esercizio</th>
              <th>Peso (kg)</th>
              <th>Ripetizioni</th>
              <th>Data</th>
            </tr>
          </thead>
          <tbody>
            {exercises.length === 0 ? (
              <tr>
                <td colSpan="4">Nessun esercizio registrato.</td>
              </tr>
            ) : (
              exercises.map((exercise, index) => (
                <tr key={index}>
                  <td>{exercise.name}</td>
                  <td>{exercise.weight} kg</td>
                  <td>{exercise.reps} reps</td>
                  <td>{new Date(exercise.date).toLocaleDateString()}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>

      {/* Form per aggiungere nuovi esercizi */}
      <ExerciseForm username={username} fetchExercises={fetchExercises} />
    </div>
  );
};

export default ExercisePage;
